    public function myFindByDate($madate)
    {
        $queryBuilder = $this->_em->createQueryBuilder()
            ->select('f')
            ->from($this->_entityName, 'f')
            ->where('f.dcreat = :madate')
            ->setParameter('madate', $madate);

        $query = $queryBuilder->getQuery();
        return $query->getResult();
    }
}
